IF NOT initMqtt THEN
	initMqtt := TRUE;
	(* Basic MQTT config *)
	MQTT_IN_OUT.ClientID := MqttVariables.clientID;

	(* Set the LWT topic / payload *)
	MQTT_IN_OUT.BuildWillTopicAndMessage := FALSE;
	MQTT_IN_OUT.WillTopicBase := '';
	MQTT_IN_OUT.ClientWillTopic := MqttVariables.MqttAvailabilityTopic;
	MQTT_IN_OUT.ClientWillMessage := MqttVariables.MqttAvailabilityOfflinePayload;

	(* Set the birth message *)
	MQTTBirthMessage.Payload := MqttVariables.MqttAvailabilityOnlinePayload;
	MQTTBirthMessage.Topic := MqttVariables.MqttAvailabilityTopic;
	MQTTBirthMessage.Qos := MqttVariables.MQTT_QOS_EXACTLY_ONCE;
	MQTTBirthMessage.MqttRetain := FALSE;

	(* Configure the rising edge detector to detect a connect/reconnect to the broker, used for  birth message *)
	MQTTConnectTrigger(CLK := MQTTInfo.MQTT_CONNECTED);

	(* Configure the dedicated MQTTBirth publisher *)
	MQTTBirthPublisher.Init(inputMQTT_IN_OUT:= ADR(MQTT_IN_OUT));

	(* Configure the pulse generator for the birth message hartbeat *)
	 MQTTBirthHartbeat(ENQ:=TRUE, PTH:=T#0S, PTL:=MqttVariables.MQTTAvailabilityHartbeatTime);

	(* INIT MQTT PUBLISH STUFF *)
	FOR icounter := 0 TO (ipublishers-1) DO		
		publishers[icounter].Init(inputMQTT_IN_OUT:= ADR(MQTT_IN_OUT));
	END_FOR 

	(* INIT MQTT SUBSCRIBE STUFF *)
	subscriber_FB_OUTPUT_SWITCH_MQTT.SetMqttInOut(MQTT_IN_OUT:= MQTT_IN_OUT);	
	subscriber_FB_OUTPUT_COVER_MQTT.SetMqttInOut(MQTT_IN_OUT:= MQTT_IN_OUT);
	subscriber_FB_VIRTUAL_MQTT.SetMqttInOut(MQTT_IN_OUT:= MQTT_IN_OUT);	
	subscriber_FB_DIMMER_MQTT.SetMqttInOut(MQTT_IN_OUT:= MQTT_IN_OUT);
	subscriber_FB_RS485_MQTT.SetMqttInOut(MQTT_IN_OUT:= MQTT_IN_OUT);
END_IF

(* set user led green if connected to MQTT broker, flashing red if not *) (* é!COCKPIT version *) (* LED_1_BrokerStatus(eID:=eLedID.AppLED_1, xOpen:=TRUE ); IF(MQTTClient.connected) THEN LED_1_BrokerStatus.SetStatic(eLedColor.Green); ELSE LED_1_BrokerStatus.SetFlash(T#1S,eLedColor.Red, eLedColor.Off); END_IF *) (* Codesys 3S version *) (* IF MQTTClient.connected THEN PFC.SetLed(which:=PFC.LED.U1, how:=PFC.LedState.STATIC_GRN); ELSE PFC.SetLed(which:=PFC.LED.U1, how:=PFC.LedState.BLINK_RED); END_IF *)
(* allow the rising edge detector to detect a connect/reconnect to the broker, used for birth message *) MQTTConnectTrigger(CLK := MQTTInfo.MQTT_CONNECTED);
(* allow the pulse generator to generate a pulse, used for birth message hartbeat *) MQTTBirthHartbeat(ENQ:=TRUE, PTH:=T#0S, PTL:=MqttVariables.MQTTAvailabilityHartbeatTime);
(* Check if this is a new connection due to startup or connection issues *) IF initMqtt AND (MQTTConnectTrigger.Q OR MQTTBirthHartbeat.Q) THEN MQTTPublishBirth := TRUE; END_IF
(* On connect or reconnect publish a birth message using the dedicated birth publisher *) IF MQTTPublishBirth THEN MQTTBirthPublisher.PublishMessage(MqttMessage := MQTTBirthMessage); MQTTPublishBirth:=FALSE; END_IF
(* Check if Mqtt is initialized AND if there are elements on the queue to process AND if a MQTTbirthmessage has been published successfully *) IF initMqtt AND NOT(MqttVariables.fbMqttPublishQueue.EMPTY) AND MQTTPublishBirth = FALSE AND MQTTBirthPublisher.SendDone THEN (* Loop through all free publishers untill there are no more elements in the queue or no more free publishers *) FOR i:=0 TO (ipublishers-1) DO (* use the publishers that are free *) IF publishers[i].SendDone AND NOT(MqttVariables.fbMqttPublishQueue.EMPTY) THEN (* Pass message from the queue to a publisher *) publishers[i].PublishMessage(MqttMessage:= MqttVariables.fbMqttPublishQueue.GetMessage()); ELSIF MqttVariables.fbMqttPublishQueue.EMPTY THEN (* Queue is empty, jump out of the for loop *) EXIT; END_IF END_FOR END_IF
(* If MQTT is initialized, let the MqttPublishWorkers do their work *) IF initMqtt THEN MQTT_IN_OUT.clientFB( MQTT_IN_OUT:= MQTT_IN_OUT, ENABLE:= TRUE, URL:= MQttVariables.broker, TIMEOUT:= T#1S, TLS:= FALSE);
MQTTBirthPublisher();
FOR i := 0 TO (ipublishers-1) DO publishers[i](); END_FOR
END_IF

(* If MQTT is initialized, let the subscriber(s) do its work *) IF initMqtt THEN
MQTT_IN_OUT.clientFB( MQTT_IN_OUT:= MQTT_IN_OUT, ENABLE:= TRUE, URL:= MqttVariables.broker, TIMEOUT:= T#1S, TLS:= FALSE);
subscriber_FB_OUTPUT_SWITCH_MQTT( Subscribe:= TRUE, Topic:= ADR(MqttVariables.MqttSubSwitchTopic), QoSSubscribe:= MqttVariables.MQTT_QOS_EXACTLY_ONCE, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_OUTPUT_SWITCH_MQTT, ); subscriber_FB_OUTPUT_COVER_MQTT( Subscribe:= TRUE, Topic:= ADR(MqttVariables.MqttSubCoverTopic), QoSSubscribe:= MqttVariables.MQTT_QOS_EXACTLY_ONCE, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_OUTPUT_COVER_MQTT, ); subscriber_FB_VIRTUAL_MQTT( Subscribe:= TRUE, Topic:= ADR(MqttVariables.MqttSubVirtualTopic), QoSSubscribe:= MqttVariables.MQTT_QOS_EXACTLY_ONCE, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_VIRTUAL_MQTT, ); subscriber_FB_DIMMER_MQTT( Subscribe:= TRUE, Topic:= ADR(MqttVariables.MqttSubDimmerTopic), QoSSubscribe:= MqttVariables.MQTT_QOS_EXACTLY_ONCE, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_DIMMER_MQTT, ); subscriber_FB_RS485_MQTT( Subscribe:= TRUE, Topic:= ADR(MqttVariables.MqttSubRS485Topic), QoSSubscribe:= MqttVariables.MQTT_QOS_EXACTLY_ONCE, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_RS485_MQTT, ); END_IF

