(* INIT COVERS STUFF *)
FB_DO_COVER_001.InitMqtt(MQTTPublishPrefix:= ADR(MqttPubCoverPrefix),				(* pointer to string prefix for the mqtt publish topic *)
	MQTTSubscribePrefix:= ADR(MqttSubCoverPrefix),									(* pointer to string prefix for the mqtt subscribe topic *)
	pMqttPublishQueue := ADR(MqttVariables.fbMqttPublishQueue),						(* pointer to MqttPublishQueue to send a new Mqtt event *)
	pMqttCallbackCollector := ADR(MqttVariables.collector_FB_OUTPUT_COVER_MQTT), 	(* pointer to CallbackCollector to receive Mqtt subscription events *)
	SD_MQTT.QoS.ExactlyOnce, 														(* specify the QoS for the POS mqtt events (values 0-100) *)
	2																				(* specify the resolution for the POS mqtt events *)
);

FB_DO_COVER_001.ConfigureFunctionBlock(T_LOCKOUT:=T#1S,								(* delay between change of direction *)
	T_UD:=T#20S																		(* run time to move the cover completely up/down *)
);

FB_DO_COVER_001(
	UP:=FB_DI_PB_013.P_LONG,
	DN:=FB_DI_PB_015.P_LONG,
	MU=>DO_031,
	MD=>DO_032	
	);

FB_DETECTPUSH( IN:= PB, SINGLE=> SINGLE, DOUBLE=> DOUBLE, TP_LONG=> LONG, LONG=> P_LONG, T_LONG:= T_Long);
P_LONG_R_TRIG(CLK:=FB_DETECTPUSH.LONG); P_LONG_F_TRIG(CLK:=FB_DETECTPUSH.LONG);
IF FB_DETECTPUSH.SINGLE AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := 'SINGLE', Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF FB_DETECTPUSH.DOUBLE AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := 'DOUBLE', Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF FB_DETECTPUSH.TP_LONG AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := 'LONG', Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF (P_LONG_R_TRIG.Q OR P_LONG_F_TRIG.Q) AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := BOOL_TO_STRING(FB_DETECTPUSH.LONG), Topic := CONCAT(CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix),'/P_LONG'), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := TRUE ); END_IF

THIS^.pMqttPublishQueue := pMqttPublishQueue;
THIS^.MqttPublishTopicPrefix := MQTTPublishPrefix;

InstanceNamePt := CommonTypesAndFunctions.FindLastDot(ADR(THIS^.InstanceName)) + 1;
THIS^.MqttPublishTopicSuffix := InstanceNamePt^;

InitMqttDone := TRUE;

IF Startup THEN
	OUT := PreviousState;
END_IF

IF PRIO_HIGH THEN
	OUT:=TRUE;
ELSIF(PRIO_LOW AND NOT (PRIO_HIGH)) THEN
	OUT:= FALSE;
ELSIF MqttHighRequest THEN
	OUT:= TRUE;
	MqttHighRequest := FALSE;
	MqttLowRequest := FALSE;
ELSIF MqttLowRequest THEN
	OUT:= FALSE;
	MqttHighRequest := FALSE;
	MqttLowRequest := FALSE;
ELSIF(TOGGLE) THEN
	OUT:=NOT(OUT);
END_IF;

IF(PreviousState <> OUT) OR Startup AND InitMqttDone THEN
	pMqttPublishQueue^.AddMessage(
		Payload := BOOL_TO_STRING(OUT),
		Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix),
		Qos := SD_MQTT.QoS.ExactlyOnce,
		MqttRetain := TRUE
	);	
	Startup:=FALSE;
END_IF	

PreviousState := OUT;

THIS^.pMqttPublishQueue := pMqttPublishQueue;
THIS^.MqttPublishTopicPrefix := MQTTPublishPrefix;

InstanceNamePt := CommonTypesAndFunctions.FindLastDot(ADR(THIS^.InstanceName)) + 1;
THIS^.MqttPublishTopicSuffix := InstanceNamePt^;
THIS^.MQTTSubscribeTopic := CONCAT(MQTTSubscribePrefix^ ,THIS^.MqttPublishTopicSuffix);

// register the FB agains the collector so mqtt events can be received
pMqttCallbackCollector^.put(instance:= THIS^);

InitMqttDone := TRUE;

//first check if Mqtt is initialized, otherwise do nothing
IF NOT(InitMqttDone) THEN
	//mark the interface call from the collector as done
	PublishReceived := TRUE;
//check if the packet is for this FB
ELSIF CommonTypesAndFunctions.StrEquals(str1:= ADR(MQTTSubscribeTopic), str2:= Data.TopicOut) THEN
	//mark the interface call from the collector as done
	PublishReceived := TRUE;
	//now process the data
	IF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('TRUE')) THEN
		MqttHighRequest := TRUE;
	END_IF
	IF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('FALSE')) THEN
		MqttLowRequest := TRUE;
	END_IF
END_IF

//need to call publish here because of FB nums
publish(
	Topic:= ADR(LocalMqttMessage.Topic), 
	PayloadString:= ADR(LocalMqttMessage.Payload), 
	PublishAsString:= TRUE, 
	QoSIn:= LocalMqttMessage.Qos, 
	MRetain:= LocalMqttMessage.MqttRetain, 
	send:= Send);

IF NOT InitDone THEN RETURN; END_IF

IF RequestToSend THEN
	Send := TRUE;
	SendDone := FALSE;
END_IF

IF publish.done THEN
	 SendDone := TRUE;
	 RequestToSend:=FALSE;
	 Send:=FALSE;
END_IF

publish.SetMqttInOut(MQTT_IN_OUT := inputMQTT_IN_OUT);

InitDone := TRUE;
SendDone := TRUE;

IF NOT EMPTY THEN
	GetMessage := fifo[pr];
	pr := OSCAT_BASIC.INC1(pr,n);
	EMPTY := pr = pw;
	FULL := FALSE;
END_IF;

IF NOT FULL THEN
	localMessage.Payload := Payload;
	localMessage.Topic := Topic;
	localMessage.Qos := Qos;
	localMessage.MqttRetain := MqttRetain;
	fifo[pw] := localMessage;
	pw := OSCAT_BASIC.INC1(pw,n);
	FULL := pw = pr;
	EMPTY := FALSE;
END_IF;

pw := pr;
FULL := FALSE;
EMPTY := TRUE;

IF NOT initMqtt THEN
	initMqtt := TRUE;
	(* Basic MQTT config *)
	MQTT_IN_OUT.ClientID := clientID;

	(* Set the LWT topic / payload *)
	MQTT_IN_OUT.BuildWillTopicAndMessage := FALSE;
	MQTT_IN_OUT.WillTopicBase := '';
	MQTT_IN_OUT.ClientWillTopic := MQTTLwtTopic;
	MQTT_IN_OUT.ClientWillMessage := MQTTLwtPayload;

	(* Set the birth message *)
	MQTTBirthMessage.Payload := MQTTBirthPayload;
	MQTTBirthMessage.Topic := MQTTBirthTopic;
	MQTTBirthMessage.Qos := SD_MQTT.QoS.ExactlyOnce;
	MQTTBirthMessage.MqttRetain := FALSE;

	(* Configure the rising edge detector to detect a connect/reconnect to the broker, used for  birth message *)
	MQTTConnectTrigger(CLK := MQTTInfo.MQTT_CONNECTED);

	(* Configure the dedicated MQTTBirth publisher *)
	MQTTBirthPublisher.Init(inputMQTT_IN_OUT:= ADR(MQTT_IN_OUT));

	(* Configure the pulse generator for the birth message hartbeat *)
	 MQTTBirthHartbeat(ENQ:=TRUE, PTH:=T#0S, PTL:=MQTTBirthHartbeatTime);

	(* INIT MQTT PUBLISH STUFF *)
	FOR icounter := 0 TO (ipublishers-1) DO		
		publishers[icounter].Init(inputMQTT_IN_OUT:= ADR(MQTT_IN_OUT));
	END_FOR 

	(* INIT MQTT SUBSCRIBE STUFF *)
	subscriber_FB_OUTPUT_SWITCH_MQTT.SetMqttInOut(MQTT_IN_OUT:= ADR(MQTT_IN_OUT));	
	subscriber_FB_OUTPUT_COVER_MQTT.SetMqttInOut(MQTT_IN_OUT:= ADR(MQTT_IN_OUT));

END_IF

(* allow the rising edge detector to detect a connect/reconnect to the broker, used for birth message *) MQTTConnectTrigger(CLK := MQTTInfo.MQTT_CONNECTED);
(* allow the pulse generator to generate a pulse, used for birth message hartbeat *) MQTTBirthHartbeat(ENQ:=TRUE, PTH:=T#0S, PTL:=MQTTBirthHartbeatTime);
(* Check if this is a new connection due to startup or connection issues *) IF initMqtt AND (MQTTConnectTrigger.Q OR MQTTBirthHartbeat.Q) THEN MQTTPublishBirth := TRUE; END_IF
(* On connect or reconnect publish a birth message using the dedicated birth publisher *) IF MQTTPublishBirth THEN MQTTBirthPublisher.PublishMessage(MqttMessage := MQTTBirthMessage); MQTTPublishBirth:=FALSE; END_IF
(* Check if Mqtt is initialized AND if there are elements on the queue to process AND if a MQTTbirthmessage has been published successfully *) IF initMqtt AND NOT(MqttVariables.fbMqttPublishQueue.EMPTY) AND MQTTPublishBirth = FALSE AND MQTTBirthPublisher.SendDone THEN (* Loop through all free publishers untill there are no more elements in the queue or no more free publishers *) FOR i:=0 TO (ipublishers-1) DO (* use the publishers that are free *) IF publishers[i].SendDone AND NOT(MqttVariables.fbMqttPublishQueue.EMPTY) THEN (* Pass message from the queue to a publisher *) publishers[i].PublishMessage(MqttMessage:= MqttVariables.fbMqttPublishQueue.GetMessage()); ELSIF MqttVariables.fbMqttPublishQueue.EMPTY THEN (* Queue is empty, jump out of the for loop *) EXIT; END_IF END_FOR END_IF
(* If MQTT is initialized, let the MqttPublishWorkers do their work *) IF initMqtt THEN MQTTClient( MQTT_IN_OUT:= MQTT_IN_OUT, ENABLE:= TRUE, URL:= broker, TIMEOUT:= T#1S, TLS:= FALSE);
MQTTBirthPublisher();
FOR i := 0 TO (ipublishers-1) DO publishers[i](); END_FOR
END_IF

(* If MQTT is initialized, let the subscriber(s) do its work *) IF initMqtt THEN MQTTClient( MQTT_IN_OUT:= MQTT_IN_OUT, ENABLE:= TRUE, URL:= broker, TIMEOUT:= T#1S, TLS:= FALSE);
subscriber_FB_OUTPUT_SWITCH_MQTT( Subscribe:= TRUE, Topic:= ADR('WAGO-PFC200/In/DigitalOutputs/+'), QoSSubscribe:= SD_MQTT.QoS.ExactlyOnce, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_OUTPUT_SWITCH_MQTT, );
subscriber_FB_OUTPUT_COVER_MQTT( Subscribe:= TRUE, Topic:= ADR('WAGO-PFC200/In/Covers/+'), QoSSubscribe:= SD_MQTT.QoS.ExactlyOnce, ExpectingString:= TRUE, Callback:= MqttVariables.collector_FB_OUTPUT_COVER_MQTT, ); END_IF

FB_DEBOUNCE(IN := BS,	
	TD := T#50MS, (* 2x cyle + 10ms *)
	PM := FALSE,
	Q=>Q);

FB_R_TRIG(CLK := FB_DEBOUNCE.Q);
FB_F_TRIG(CLK := FB_DEBOUNCE.Q);

EVENT := FB_R_TRIG.Q OR FB_F_TRIG.Q;
EVENT_R := FB_R_TRIG.Q;
EVENT_F := FB_F_TRIG.Q;

IF FB_R_TRIG.Q AND InitMqttDone THEN
	pMqttPublishQueue^.AddMessage(
		Payload := 'ON',
		Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix),
		Qos := SD_MQTT.QoS.ExactlyOnce,
		MqttRetain := TRUE
	);
END_IF

IF FB_F_TRIG.Q AND InitMqttDone THEN
	pMqttPublishQueue^.AddMessage(
		Payload := 'OFF',
		Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix),
		Qos := SD_MQTT.QoS.ExactlyOnce,
		MqttRetain := TRUE
	);
END_IF

THIS^.pMqttPublishQueue := pMqttPublishQueue;
THIS^.MqttPublishTopicPrefix := MQTTPublishPrefix;

InstanceNamePt := CommonTypesAndFunctions.FindLastDot(ADR(THIS^.InstanceName)) + 1;
THIS^.MqttPublishTopicSuffix := InstanceNamePt^;

InitMqttDone := TRUE;

FB_DIMMER( IN:= PB, SET:= SET, VAL:= VAL, RST:= RST, OUT:= DIM, T_DEBOUNCE:= T_Debounce, T_RECONFIG:= T_Reconfig, T_ON_MAX:= T_On_Max, T_DIMM_START:= T_Dimm_Start, T_DIMM:= T_Dimm, MIN_ON:= Min_On, MAX_ON:= Max_On, SOFT_DIMM:= Soft_Dimm, DBL_TOGGLE:= Dbl_Toggle, RST_OUT:= Rst_Out, Q=> Q, DBL=> DBL);
FB_DETECTPUSH( IN:= PB, SINGLE=> SINGLE, DOUBLE=> DOUBLE, TP_LONG=> LONG, T_LONG:= T_long);
P_LONG_R_TRIG(CLK:=FB_DETECTPUSH.LONG); P_LONG_F_TRIG(CLK:=FB_DETECTPUSH.LONG);
IF (ABS(BYTE_TO_INT(PreviousDim) - BYTE_TO_INT(DIM)) >= Delta_Dimm OR PB = FALSE) AND DIM <> PreviousDim AND InitMqttDone AND OutputDimmer THEN PreviousDim:=DIM; pMqttPublishQueue^.AddMessage( Payload := BYTE_TO_STRING(DIM), Topic := CONCAT(CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), '/DIM'), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF FB_DIMMER.Q <> PreviousQ AND InitMqttDone THEN PreviousQ:=FB_DIMMER.Q; pMqttPublishQueue^.AddMessage( Payload := BOOL_TO_STRING(FB_DIMMER.Q), Topic := CONCAT(CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), '/Q'), Qos := Qos_Dimm, MqttRetain := TRUE ); END_IF
IF FB_DIMMER.DBL <> PreviousDBL AND InitMqttDone THEN PreviousDBL:=FB_DIMMER.DBL; pMqttPublishQueue^.AddMessage( Payload := BOOL_TO_STRING(FB_DIMMER.DBL), Topic := CONCAT(CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), '/DBL'), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := TRUE ); END_IF
IF FB_DETECTPUSH.SINGLE AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := 'SINGLE', Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF FB_DETECTPUSH.DOUBLE AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := 'DOUBLE', Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF FB_DETECTPUSH.TP_LONG AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := 'LONG', Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := FALSE ); END_IF
IF (P_LONG_R_TRIG.Q OR P_LONG_F_TRIG.Q) AND InitMqttDone THEN pMqttPublishQueue^.AddMessage( Payload := BOOL_TO_STRING(FB_DETECTPUSH.LONG), Topic := CONCAT(CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix),'/P_LONG'), Qos := SD_MQTT.QoS.ExactlyOnce, MqttRetain := TRUE ); END_IF

THIS^.pMqttPublishQueue := pMqttPublishQueue;
THIS^.MqttPublishTopicPrefix := MQTTPublishPrefix;

InstanceNamePt := CommonTypesAndFunctions.FindLastDot(ADR(THIS^.InstanceName)) + 1;
THIS^.MqttPublishTopicSuffix := InstanceNamePt^;

THIS^.OutputDimmer:=OutputDimmer;
THIS^.Qos_Dimm:=Qos_Dimm;
THIS^.Delta_Dimm:=Delta_Dimm;

InitMqttDone := TRUE;

THIS^.T_Debounce:=T_Debounce;
THIS^.T_Reconfig:=T_Reconfig;
THIS^.T_On_Max:=T_On_Max;
THIS^.T_Dimm_Start:=T_Dimm_Start;
THIS^.T_Dimm:=T_Dimm;
THIS^.Min_On:=Min_On;
THIS^.Max_On:=Max_On;

THIS^.T_Long:=T_Long;

(* read system timer *)
tx := DWORD_TO_TIME(OSCAT_BASIC.T_PLC_MS());

IF E AND init AND (dir = last_dir) AND (RMP <> SEL(DIR, 0, 255)) AND TR = tn THEN
	RMP := OSCAT_BASIC.FRMP_B(start, DIR, tx - tl, TR);
ELSE
	init := TRUE;
	tl := tx;
	tn := tr;
	start := RMP;
END_IF;
last_dir := dir;



(* revison history
hm	22. oct. 2008	rev 1.0
	original version

hm	20. nov. 2008	rev 1.1
	set default for E to be TRUE
	added init

hm	19. nov. 2011	rev 1.2
	new code
*)

(* make sure only one motor is active at a given time *)
lock(i1 := UP, I2 := DN, TL := T_lockout);

(* ramp up or down to simulate the angle position of the blind slats *)
angle(e := lock.Q1 OR lock.Q2, UP := lock.Q1, PT := T_Angle);
position(e := lock.Q1 AND angle.high OR lock.Q2 AND angle.low, up := lock.Q1, PT := T_UD);

(* set the outputs *)
pos := position.Out;
ang := angle.Out;

(* set the outputs *)
QU := lock.Q1;
QD := lock.Q2;

(* set the status output *)
IF UP AND DN THEN
	 status := 1;			(* error up and down together are not allowed *)
ELSIF UP THEN
	status := 121;
ELSIF DN THEN
	status := 122;
ELSE
	status := S_IN;
END_IF;


(* revision history
hm	29. sep 2007	rev 1.0
	original release

hm	6. oct 2007	rev 1.1
	rearanged outputs

hm	18. oct. 2008	rev 1.2
	changed calls for rmp_B because of change in RMP_B

hm	27. jul. 2009	rev 1.3
	changes status to be 12X

ae	08. mar. 2010 rev 1.4	
	at aPosition

*)

IF Startup THEN
	POS:=PreviousPos;
	FB_ACT.SetPOS(PreviousPos);
END_IF

Cover_State_Timer(PT:=2 * T_UD);
Cover_State_Timer.IN:=FALSE;


CASE Cover_State OF
	COVER_STATES.Idle:
		internal_UP:=FALSE;
		internal_DN:=FALSE;
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	COVER_STATES.Prio_up:
		// Only power an engine for maximum twice T_UD
		IF Cover_State_Timer.Q THEN
			internal_UP:=TRUE;
			internal_DN:=FALSE;
		ELSE
			internal_UP:=FALSE;
			internal_DN:=FALSE;
		END_IF
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	COVER_STATES.Prio_dn:
		// Only power an engine for maximum twice T_UD
		IF Cover_State_Timer.Q THEN
			internal_UP:=FALSE;
			internal_DN:=TRUE;
		ELSE
			internal_UP:=FALSE;
			internal_DN:=FALSE;
		END_IF
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	COVER_STATES.Auto:
		internal_UP:=TRUE;
		internal_DN:=TRUE;
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	COVER_STATES.Manual:
		internal_UP:=UP;
		internal_DN:=DN;
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	COVER_STATES.Calibrate_up:
		// Only power an engine for maximum twice T_UD
		IF Cover_State_Timer.Q THEN
			internal_UP:=FALSE;
			internal_DN:=TRUE;
		ELSE
			internal_UP:=FALSE;
			internal_DN:=FALSE;
			//Finish the request
			MqttRequestCalibrateUp:=FALSE;
		END_IF
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	COVER_STATES.Calibrate_dn:
		// Only power an engine for maximum twice T_UD
		IF Cover_State_Timer.Q THEN
			internal_UP:=FALSE;
			internal_DN:=TRUE;
		ELSE
			internal_UP:=FALSE;
			internal_DN:=FALSE;
			//Finish the request
			MqttRequestCalibrateDn:=FALSE;
		END_IF
		//Calculate the next state
		Cover_State := CALC_COVER_STATE(UP:=UP,DN:=DN,PRIO_UP:=PRIO_UP,PRIO_DN:=PRIO_DN,PI:=PI,POS:=POS,MqttRequestAuto:=ADR(MqttRequestAuto),MqttRequestStop:=ADR(MqttRequestStop),MqttRequestCalibrateUp:=ADR(MqttRequestCalibrateUp),MqttRequestCalibrateDn:=ADR(MqttRequestCalibrateDn),CurrentState:=Cover_State,StateTimer:=ADR(Cover_State_Timer));
	ELSE
		Cover_State:=COVER_STATES.Idle;
END_CASE

(* Check Position*)
FB_ACT(T_UD:=T_UD, T_ANGLE:=T#0S, T_lockout := T_Lockout);

IF internal_UP AND internal_DN THEN
	(* automatic modus detected *)
	(* first find correct position *)
	IF BYTE_TO_INT(FB_ACT.pos) < BYTE_TO_INT(pi) - delta THEN
		FB_ACT.UP := TRUE;
		FB_ACT.DN := FALSE;
		delta := 0;
	ELSIF BYTE_TO_INT(FB_ACT.pos) > BYTE_TO_INT(pi) + delta THEN
		FB_ACT.UP := FALSE;
		FB_ACT.DN := TRUE;
		delta := 0;
	(* correct position reached *)
	ELSE
		FB_ACT.UP := FALSE;
		FB_ACT.DN := FALSE;
		delta := sens;
	END_IF;
ELSE
	FB_ACT.UP := internal_UP;
	FB_ACT.DN := internal_DN;
END_IF;

(* blind control calls blind_actuator *)
FB_ACT(T_UD:=T_UD, T_ANGLE:=T#0S, T_lockout := T_Lockout);
POS := FB_ACT.pos;
MU := FB_ACT.QU;
MD := FB_ACT.QD;

IF (ABS(BYTE_TO_INT(PreviousPos) - BYTE_TO_INT(POS)) >= Delta_POS  OR (MU = FALSE AND MD = FALSE)) AND POS <> PreviousPos OR Startup AND InitMqttDone THEN
	PreviousPos:=POS;
	pMqttPublishQueue^.AddMessage(
		Payload := INT_TO_STRING(REAL_TO_INT(OSCAT_BASIC.SCALE_B(X:=POS, I_LO:=0, I_HI:=255, O_LO:=0, O_HI:=100))),
		Topic := CONCAT(MqttPublishTopicPrefix^, MqttPublishTopicSuffix),
		Qos := Qos_POS,
		MqttRetain := TRUE
	);
	Startup:=FALSE;
END_IF

THIS^.pMqttPublishQueue := pMqttPublishQueue;
THIS^.MqttPublishTopicPrefix := MQTTPublishPrefix;

InstanceNamePt := CommonTypesAndFunctions.FindLastDot(ADR(THIS^.InstanceName)) + 1;
THIS^.MqttPublishTopicSuffix := InstanceNamePt^;
THIS^.MQTTSubscribeTopic := CONCAT(MQTTSubscribePrefix^ ,THIS^.MqttPublishTopicSuffix);

// register the FB agains the collector so mqtt events can be received
pMqttCallbackCollector^.put(instance:= THIS^);

THIS^.Qos_POS:=Qos_POS;
THIS^.Delta_POS:=REAL_TO_INT(OSCAT_BASIC.SCALE_B(X:=INT_TO_BYTE(Delta_POS), I_LO:=0, I_HI:=100, O_LO:=0, O_HI:=255));

InitMqttDone := TRUE;

//first check if Mqtt is initialized, otherwise do nothing
IF NOT(InitMqttDone) THEN
	//mark the interface call from the collector as done
	PublishReceived := TRUE;
//check if the packet is for this FB
ELSIF CommonTypesAndFunctions.StrEquals(str1:= ADR(MQTTSubscribeTopic), str2:= Data.TopicOut) THEN
	//mark the interface call from the collector as done
	PublishReceived := TRUE;
	//now process the data
	IF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('STOP')) THEN			
		THIS^.MqttRequestStop:=TRUE;
	ELSIF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('OPEN')) THEN
		THIS^.PI:=255;
		THIS^.MqttRequestAuto:=TRUE;
	ELSIF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('CLOSE')) THEN		
		THIS^.PI:=0;
		THIS^.MqttRequestAuto:=TRUE;
	ELSIF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('CAL_UP')) THEN
		THIS^.MqttRequestCalibrateUp:=TRUE;
	ELSIF CommonTypesAndFunctions.StrEquals(str1:= Data.PayloadString, str2:= ADR('CAL_DN')) THEN
		THIS^.MqttRequestCalibrateDn:=TRUE;
	ELSIF OSCAT_BASIC.IS_NUM(str:= Data.PayloadString^) THEN
		THIS^.PI:=REAL_TO_BYTE(OSCAT_BASIC.SCALE_B(X:=STRING_TO_BYTE(Data.PayloadString^), I_LO:=0, I_HI:=100, O_LO:=0, O_HI:=255));
		THIS^.MqttRequestAuto:=TRUE;
	END_IF

END_IF

IF PRIO_UP THEN
	NewState := COVER_STATES.Prio_up;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF PRIO_DN THEN
	NewState := COVER_STATES.Prio_dn;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF UP OR DN THEN
	NewState := COVER_STATES.Manual;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF MqttRequestStop^ THEN
	NewState := COVER_STATES.Idle;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF MqttRequestAuto^ AND PI <> POS THEN
	NewState := COVER_STATES.Auto;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF MqttRequestAuto^ AND PI = POS THEN
	NewState := COVER_STATES.Idle;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF MqttRequestCalibrateUp^ THEN
	NewState := COVER_STATES.Calibrate_up;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSIF MqttRequestCalibrateDn^ THEN
	NewState := COVER_STATES.Calibrate_dn;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
ELSE
	NewState := COVER_STATES.Idle;
	MqttRequestAuto^ := FALSE;
	MqttRequestStop^ := FALSE;
	MqttRequestCalibrateUp^ := FALSE;
	MqttRequestCalibrateDn^ := FALSE;
END_IF

IF NewState <> CurrentState THEN
	//Reset StateTimer
	StateTimer^.IN:=TRUE;
END_IF

CALC_COVER_STATE := NewState;
